function showSynopsis(id) {
  var dataUser = {
    function: "showSynopsis",
    chapter_id: id
  };
  $.ajax({
    type: "POST",
    data: dataUser,
    url: "./adapters/program.php",
    success: function success(result) {
      var json = JSON.parse(result);

      if (json.code == 200) {
        localStorage.setItem("synopsis", JSON.stringify(json.data));
        location.href = "./sinopsis.php";
      } else {
        var noSynopsis = "
            <div class="no-synopsis-container text-center">
                <img src="./images/sinopsis/helmet.png" alt="" class="helmet-image">
                <h3 class="no-synopsis-title a-text-white-monblack">SIN\xD3PSIS EN <span>CONSTRUCCI\xD3N</span></h3>
                <p class="no-synopsis-text a-text-white-semibold">La sin\xF3psis de este programa no est\xE1 disponible a\xFAn</p>
            </div>
          ";
        $(".synopsis-content").addClass("col-12").html(noSynopsis);
      }
    }
  });
}

