    <style type="text/css" data-type="vc_custom-css">
        #thumbnail_row img {
            width: 100%;
        }

        #thumbnail_row a {
            outline: 0;
        }

        span#thumbnail-title {
            margin: 5px 0 10px 0;
            display: block;
            font-size: 15px;
            line-height: 20px
        }

        /*********************
social links icons
*********************/
        .w-socials-item-link {
            line-height: 4.5em;
            width: 4.5em;
        }

        .w-socials-item.facebook .w-socials-item-link:after,
        .w-socials-item.instagram .w-socials-item-link:after,
        .w-socials-item.twitter .w-socials-item-link:after {
            font-size: 2em;
        }

        /********************************/
        .heading-img,
        .heading-img-2 {
            color: #d43d67 !important;
        }

        /*new Header image background & position*/
        .heading-img {
            background: url("https://3dl0u21nnqk62nshp111jz32-wpengine.netdna-ssl.com/wp-content/uploads/2019/04/NV_Pleca-3.png") center no-repeat;
            background-size: cover;
            /*padding: 50px 0;*/
        }

        h1.heading-img {
            padding: 50px 0;
        }

        .heading-img-2 {
            background: url("https://3dl0u21nnqk62nshp111jz32-wpengine.netdna-ssl.com/wp-content/uploads/2019/04/NV_Pleca-4.png") center no-repeat;
            background-size: cover;
        }

        h1.heading-img-2 {
            padding: 35px 0;
        }

        #heading-position-2 {
            position: relative;
            top: -100px;
        }

        /****************************
font-weight landing page
****************************/
        h4,
        .widgettitle {
            font-weight: 400px !important;
        }

        #whatsnew {
            font-weight: bold;
        }

        /***
.becomaeaffiliate_btn{
    background:#e964aa;
}
/*region content position*/
        #intro .l-section-h {
            /*margin-top:-370px !important;*/
            padding-bottom: 0px;
            padding-top: 0px !important;
        }

        .linkwrap {
            display: inline-block;
        }

        .blocker {
            position: absolute;
            height: 100%;
            width: 100%;
            z-index: 3;
            top: 0;
            left: 0;
            background: rgba(255, 0, 0, 0.0);
        }

        .linkwrap iframe {
            z-index: 2;
        }

        #availability_text h3,
        #availability_text h4 {
            /*line-height:106px;*/
            color: white;
            text-align: center;
            padding-bottom: 0.5rem !important;
        }

        /**********************************
Regions Of Availability
**********************************/
        section#intro {
            padding-bottom: 150px;
        }

        #areas_text h5 {
            margin-bottom: -5px;
        }

        .regions_col {
            padding: 0;
        }

        /*borders around regions box*/
        #regions_col div.vc_column-inner {
            /*border-radius: 2px;*/
            border: 3px solid #fff;
            box-shadow: 1px 1px 10px #00000047;
            margin: 1px;
        }

        #territory_link a {
            color: #fff;
            cursor: pointer;
            display: block;
            border-bottom: none;
        }

        .territory_green_box {
            background-color: #10d0a3;
        }

        .territory_red_box {
            background-color: #da4265;
        }

        .territory_purple_box {
            background-color: #d9398d;
        }

        .territory_blue_box {
            background-color: #3cafef;
        }

        .territory_container_green>div,
        .territory_container_red>div,
        .territory_container_blue>div,
        .territory_container_pink>div {
            transition: all .4s ease-in-out;
            cursor: pointer;
        }

        .territory_container_green>div:hover {
            background-color: #0e9a79 !important;
        }

        .territory_container_red>div:hover {
            background-color: #a0304a !important;
        }

        .territory_container_blue>div:hover {
            background-color: #318fc3 !important;
        }

        .territory_container_pink>div:hover {
            background-color: #b32e74 !important;
        }

        #headertitle_mobile {
            color: #fff;
            font-size: 30px;
        }

        /***/
        /*media queries*/
        @media screen and (max-width: 1024px) {

            h1.heading-img,
            h1.heading-img-2 {
                font-size: 30px;
            }

            /*#heading-position{
        top:-183px;
}*/
            #heading-position-2 {
                top: -85px;
            }

            .w-socials-item-link {
                line-height: 2.5em;
                width: 2.5em;
            }

            .w-socials-item.facebook .w-socials-item-link:after,
            .w-socials-item.instagram .w-socials-item-link:after,
            .w-socials-item.twitter .w-socials-item-link:after {
                font-size: 1.5em;
            }
        }

        @media screen and (min-width:768px) {
            #mobile-hero-section {
                display: none !important;
            }

            #mobile-hero-section.l-section-h {
                padding: 0 !important;
            }

            .regions_col {
                width: 90%;
                margin: 0 auto 1rem;
            }
        }

        @media screen and (max-width:767px) {
            #hero-section {
                display: none !important;
            }
        }

        @media screen and (max-width:1024px) and (min-width:768px) {
            section#intro {
                padding-bottom: 170px;
            }
        }

        @media screen and (max-width:768px) and (min-width:641px) {
            section#intro {
                padding-bottom: 140px;
            }
        }

        @media screen and (max-width:641px) {
            section#intro {
                padding-bottom: 70px;
            }

            h1.heading-img,
            h1.heading-img-2 {
                display: none;
            }

            /*#social-row{
        display:none;
    }*/
            h3.subheading {
                font-size: 25px !important;
            }
        }

        /*@media screen and (max-width:768px){
    #social-row{
        display:block;
    }
}*/
        @media screen and (min-width:768px) {
            #ampliamos-header-mobile {
                display: none;
            }
        }
